var leftValue = this.view.FlexContainerSideMenu.left;

if (leftValue === "-180dp") {
  // Slide in
  this.view.FlexContainerSideMenu.animate(
    kony.ui.createAnimation({
      "100": { "left": "0dp", "stepConfig": { "timingFunction": kony.anim.EASE } }
    }),
    {
      "duration": 0.3,
      "iterationCount": 1,
      "fillMode": kony.anim.FILL_MODE_FORWARDS
    },
    { animationEnd: function() {} }
  );
} else {
  // Slide out
  this.view.FlexContainerSideMenu.animate(
    kony.ui.createAnimation({
      "100": { "left": "-180dp", "stepConfig": { "timingFunction": kony.anim.EASE } }
    }),
    {
      "duration": 0.3,
      "iterationCount": 1,
      "fillMode": kony.anim.FILL_MODE_FORWARDS
    },
    { animationEnd: function() {} }
  );
}
